CREATE PROCEDURE blackstarDb.`CodexCustomerTransfer`(pCorporateName TEXT, pContactName TEXT, pCstId INT, pClientTypeId INT, pEmail VARCHAR(200), pStreet TEXT,
														pExtNumber VARCHAR(100), pIntNumber VARCHAR(100), pNeighborhood TEXT, pCity TEXT, pState TEXT, pCountry TEXT, pZipCode INT, pPhoneArea VARCHAR(50),
														pPhoneNumber VARCHAR(50), pPhoneExtension VARCHAR(50), pPhoneNumberAlt VARCHAR(50), pRfc VARCHAR(100), pClientOriginId INT, pIsProspect INT)
BEGIN

  IF(SELECT COUNT(*) FROM codexClient WHERE corporateName = pCorporateName) = 0 THEN
     INSERT INTO codexClient (corporateName, contactName, cstId, clientTypeId, email, street,
								extNumber, intNumber, neighborhood, city, state, country, zipCode, phoneArea,
								phoneNumber, phoneExtension, phoneNumberAlt, rfc, clientOriginId, isProspect)
                 VALUES
                 (pCorporateName, pContactName, pCstId, pClientTypeId, pEmail, pStreet,
					pExtNumber, pIntNumber, pNeighborhood, pCity, pState, pCountry, pZipCode, pPhoneArea,
					pPhoneNumber, pPhoneExtension, pPhoneNumberAlt, pRfc, pClientOriginId, pIsProspect);
  ELSE
      UPDATE codexClient SET clientTypeId = pClientTypeId, clientOriginId = pClientOriginId,
      		cstId = pCstId, isProspect = pIsProspect, rfc = pRfc, tradeName = pCorporateName, 
			phoneArea = pPhoneArea, phoneNumber = pPhoneNumber, phoneExtension = pPhoneExtension, phoneNumberAlt = pPhoneNumberAlt, 
			email = pEmail, street = pStreet, extNumber = pExtNumber, intNumber = pIntNumber, 
			zipCode = pZipCode, country = pCountry, state = pState, municipality = pCity, city = pCity, 
			neighborhood = pNeighborhood, contactName = pContactName
      WHERE corporateName = pCorporateName;
  END IF;
	
END$$
